function getDurationSecsFromTasmotaTime(tasmTime) {
    const { Duration } = global.get('luxon');
    const dtMatches = tasmTime.match(/(\d+)T(\d+):(\d+):(\d+)/);
    const dtDuration = Duration.fromObject({
        days: dtMatches[1],
        hours: dtMatches[2], 
        minutes: dtMatches[3],
        seconds: dtMatches[4]
    });
    return dtDuration.as('seconds');
}

